	class SingleLinked &#123;
		private Node _head;
		private Node _tail;
		private Node _currentNode;
		private int _size;
		public SingleLinked (int head) &#123;
			_head = new Node(head);
			_tail = _head;
			_currentNode = _head;
			_size = 0;
		&#125;

		public Node Find (int value) &#123;
			_currentNode = _head;

			while(_currentNode != null &amp;&amp; _currentNode?.value != value) &#123;
				_currentNode = _currentNode.next;
			&#125;

			if (_currentNode == null) &#123;
				Console.WriteLine(&quot;Node not found&quot;);
				return null;
			&#125;

			return _currentNode;
		&#125;

		public void Add (int value) &#123;
			_currentNode = _tail;
			_currentNode.next = new Node(value);
			_tail = _currentNode.next;
			_tail.prev = _currentNode;
			_size++;
		&#125;

		// åœ¨æŒ‡å®šçš„ node å‰æ’å…¥ value
		public void Insert (int value, int node) &#123;
			_currentNode = this.Find(node);
			if (_currentNode == null) &#123;
				Console.WriteLine(&quot;Node not found&quot;);
				return;
			&#125;

			Node val = new(value);
			val.next = _currentNode;
			val.prev = _currentNode.prev;
			_currentNode.prev.next = val;
			_currentNode.prev = val;
			_size++;
		&#125;

		public void Remove (int value) &#123;
			if (value == _tail.value) &#123;
				_currentNode = _tail.prev;
				_currentNode.next = null;
				_size--;
			&#125;

			if (value == _head.value) &#123;
				if (_size == 0) &#123;
					return;
				&#125; else &#123;
					_currentNode = _head;
					_head.next = null;
					_size = 0;
				&#125;
			&#125;

			_currentNode = this.Find(value);
			if (_currentNode == null) return;
			_currentNode.prev.next = _currentNode.next;
			_currentNode.next.prev = _currentNode.prev;
			_currentNode = _head;
			_size--;
		&#125;

		public Node Current () &#123;
			Console.WriteLine(_currentNode.value);
			return _currentNode;
		&#125;

		public int GetLength () &#123;
			return _size;
		&#125;

