		public void Forward (int num, int? node = null) &#123;
			if ( node == null) &#123;
				_currentNode = _head;
			&#125; else &#123;
				_currentNode = this.Find((int)node);
				if (_currentNode == null) &#123;
					Console.WriteLine(&quot;Node not found&quot;);
					return;
				&#125;
			&#125;
			while (num-- != 0 &amp; _currentNode.next != null) &#123;
				_currentNode = _currentNode.next;
			&#125;
			return;
		&#125;

