			&#125; else &#123;
				_currentNode = this.Find((int)node);
				if (_currentNode == null) &#123;
					Console.WriteLine(&quot;Node not found&quot;);
					return;
				&#125;
			&#125;
			while (num-- != 0 &amp; _currentNode.prev != null) &#123;
				_currentNode = _currentNode.prev;
			&#125;
			return;
